import axios from 'axios';
export default {
  name: 'app',
  data: function data() {
    return {
      games: {},
      posts: [],
      errors: []
    };
  },
  created: function created() {
    // Fetches posts when the component is created.
    this.getgames();
  },
  methods: {
    getgames: function getgames() {
      var _this = this;

      axios.get('http://35.184.8.206:8082/api/juegos').then(function (response) {
        // JSON responses are automatically parsed.
        _this.posts = response.data;
      }).catch(function (e) {
        _this.errors.push(e);
      });
    },
    getonegame: function getonegame(idgame) {
      var _this2 = this;

      axios.get('http://35.184.8.206:8082/api/juegos/' + idgame).then(function (response) {
        // JSON responses are automatically parsed.
        _this2.product = response.data;
        alert("status: " + response.status + ", selected: " + JSON.stringify(response.data));
      }).catch(function (e) {
        _this2.errors.push(e);
      });
    },
    addGame: function addGame() {
      var _this3 = this;

      alert(this.games.nombre + this.games.plataforma + this.games.precio);
      axios.post('http://35.184.8.206:8082/api/juegos', this.games).then(function (response) {
        alert("status: " + response.status + ", inserted: " + JSON.stringify(response.data));
      }).catch(function (e) {
        _this3.errors.push(e);
      });
    },
    updateGame: function updateGame() {
      var _this4 = this;

      //alert(idgame);
      alert(this.game.id);
      axios.put('http://35.184.8.206:8082/api/juegos/' + this.games.id, this.game).then(function (response) {
        alert("status: " + response.status + ", updated: " + JSON.stringify(response.data));

        _this4.getgames();
      }).catch(function (e) {
        _this4.errors.push(e);
      });
    },
    deleteGame: function deleteGame(idgame) {
      var _this5 = this;

      alert("delete" + idgame);
      axios.delete('http://35.184.8.206:8082/api/juegos/' + idgame).then(function (response) {
        alert("status: " + response.status + ", deleted: " + JSON.stringify(response.data));

        _this5.getgames();
      }).catch(function (e) {
        _this5.errors.push(e);
      });
    }
  }
};
